function makeid(length) {
    let result = '';
    const characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
    const charactersLength = characters.length;
    let counter = 0;
    while (counter < length) {
      result += characters.charAt(Math.floor(Math.random() * charactersLength));
      counter += 1;
    }
    return result;
}

function new_wp(line){
    return {
        name:line.name || makeid(5),
        lat:line.lat || -1,
        lon:line.lon || -1,
        depth:line.depth || 0,
        rpm:line.rpm || 0,
        speed:line.speed || 0,
        use_rpm:line.use_rpm || true,
        tolerance:line.tolerance || 1.5,
        arrival_heading:line.arrival_heading || null,
        use_heading: line.use_heading || false
    }
}

